parameters.text:
=Below is a  job alert email received from upwork. Please extract the requsted information in valid JSON format.

subject: {{ $('Gmail Trigger').item.json.subject }}
Message Body:{{ $json.markdown }}

parameters.inputSchema:
{
  "type": "object",
  "properties": {
    "state": {
      "type": "string"
    },
    "cities": {
      "type": "array",
      "items": {
        "type": "string"
      }
    },
    "jobName": {
      "type": "string"
    },
    "jobType": {
      "enum": ["Hourly", "Fixed"]
    },
    "price": {
      "type": "string"
    },
    "jobDetailUrl": {
      "type": "string"
    },
    "descriptionSnippet": {
      "type": "string",
      "description": "The description of the job before the 'more' link"
    },
    "country": {
      "type": "string"
    },
    "paymentVerified": {
      "type": "boolean"
    },
    "clientRating": {
      "type": "number"
    },
    "amountSpent": {
      "type": "string"
    },
    "tags": {
      "type": "array",
      "description": "List of categories with a link typically found below the country. Just include the tag name, not the url.",
      "items": {
        "type": "string"
      }
    },
    "postedAt": {
      "type": "string"
    },
    "experienceLevel": {
      "enum": ["Entry", "Intermediate", "Expert"]
    }
  },
  "required": [
    "jobName",
    "jobType",
    "jobDetailUrl",
    "price",
    "descriptionSnippet",
    "country",
    "paymentVerified",
    "clientRating",
    "amountSpent",
    "postedAt",
    "experienceLevel"
  ],
  "additionalProperties": false
}

